/** Box Model  */

/** Import everything from autoload */

@import url(./autoload/foundation.min.css);

/**
 * Import npm dependencies
 *
 * Prefix your imports with `~` to grab from node_modules/
 * @see https://github.com/webpack-contrib/sass-loader#imports
 */

/** Import theme styles */

@import url(https://fonts.googleapis.com/css?family=Montserrat:300,400,700,900);

@import url(https://fonts.googleapis.com/css?family=Libre+Baskerville:400i);

.block {
  padding: 0 20px;
}

.block--bg-primary {
  background-color: #ffcf21;
}

.block--bg-primary .h2-strong,
.block--bg-primary .block-desc .title-link,
.block--bg-primary .link:hover {
  color: white !important;
}

.block--bg-primary .arrow,
.block--bg-primary .arrow:before,
.block--bg-primary .arrow:after {
  background-color: white;
}

.container {
  position: relative;
}

.block .container .content {
  max-width: 1300px;
  margin: 0 auto;
}

.left-mail {
  transform: rotate(-90deg);
  position: fixed;
  left: -77px;
  top: 50%;
}

.left-mail a {
  color: #273c4e;
}

.right-phone {
  transform: rotate(90deg);
  position: fixed;
  right: -30px;
  top: 50%;
}

.right-phone a {
  color: #273c4e;
}

@media (min-width: 992px) {
  .wrap.container {
    margin: 40px;
  }
}

body {
  font-family: "Montserrat", "Helvetica Neue", Helvetica, Roboto, Arial, sans-serif;
  font-weight: normal;
  font-size: 16px;
  color: #273c4e;
}

.h2 {
  font-size: 55px;
  font-weight: lighter;
}

.h2-strong {
  font-weight: 900;
  color: #ffcf21;
  text-decoration: underline;
}

.h1,
.h2,
.h3,
.h4,
.h5,
.h6,
h1,
h2,
h3,
h4,
h5,
h6 {
  font-family: "Montserrat", "Helvetica Neue", Helvetica, Roboto, Arial, sans-serif;
}

a.link {
  font-weight: bold;
  color: #273c4e;
  transition: all 300ms;
}

a.link:hover {
  color: #ffcf21;
}

.i {
  font-family: 'Libre Baskerville', serif;
  font-style: italic;
}

.full-h {
  height: auto;
  min-height: 100vh;
}

.center-xy {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}

.rounded {
  border-radius: 50%;
}

.mh1 {
  max-height: 1px;
}

.br {
  display: block;
}

.bg-primary {
  background-color: #ffcf21;
}

.bg-secondary {
  background-color: #273c4e;
}

.hidden {
  opacity: 0 !important;
}

.overflow-hidden {
  overflow: hidden !important;
}

@media (max-width: 767px) {
  .full-h {
    min-height: 100vh;
  }
}

.btn {
  transition: all 300ms;
  cursor: pointer;
}

.btn.white {
  background-color: white;
}

.btn.white:hover {
  background-color: #ffcf21;
}

.btn.primary {
  background-color: #ffcf21;
}

.btn.primary:hover {
  background-color: #273c4e;
}

.btn-rounded-icon {
  border-radius: 50%;
  width: 35px;
  height: 35px;
}

/** Search form */

.bulle {
  font-weight: 900;
  font-size: 24px;
  background-color: white;
  color: #ffcf21;
  position: absolute;
  top: -20px;
  width: fit-content;
  left: 80%;
  padding: 10px 25px;
  border-radius: 15px;
  filter: drop-shadow(5px 3px 6px rgba(0, 0, 0, 0.2));
}

.bulle:before {
  content: '';
  position: absolute;
  display: inline-block;
  left: 0px;
  bottom: -11px;
  width: 26px;
  height: 35px;
  background-image: url(/wex-wp/wp-content/themes/wex/dist/images/pointe-bulle.svg);
  background-repeat: no-repeat;
  background-position: bottom left;
}

.arrow {
  background-color: #ffcf21;
  vertical-align: middle;
  transition: all 300ms;
  position: relative;
  display: inline-block;
}

.arrow::after,
.arrow::before {
  background-color: #ffcf21;
  transition: all 300ms;
  content: '';
  position: absolute;
}

.arrow-right {
  width: 15px;
  height: 1px;
  margin-right: 15px;
}

.arrow-right::after,
.arrow-right::before {
  width: 1px;
  height: 6px;
  bottom: -4px;
}

.arrow-right:before {
  top: 0px;
  right: 2px;
  transform: rotate(45deg);
}

.arrow-right:after {
  right: 2px;
  bottom: 0px;
  transform: rotate(-45deg);
}

.arrow-down {
  width: 1px;
  height: 15px;
}

.arrow-down::after,
.arrow-down::before {
  width: 1px;
  height: 6px;
  right: -4px;
}

.arrow-down:before {
  left: 2px;
  bottom: -1px;
  transform: rotate(45deg);
}

.arrow-down:after {
  right: 2px;
  bottom: -1px;
  transform: rotate(-45deg);
}

.btn.white:hover .arrow {
  background: white;
}

.btn.white:hover .arrow:after,
.btn.white:hover .arrow::before {
  background: white;
}

.btn.white .arrow {
  background: #ffcf21;
}

.btn.white .arrow:after,
.btn.white .arrow::before {
  background: #ffcf21;
}

.btn.primary:hover .arrow {
  background: white;
}

.btn.primary:hover .arrow:after,
.btn.primary:hover .arrow::before {
  background: white;
}

.btn.primary .arrow {
  background: white;
}

.btn.primary .arrow:after,
.btn.primary .arrow::before {
  background: white;
}

.navigation_navbar .logo_digitaleez {
  width: 53px;
  height: 58px;
}

.navigation_navbar.open .hamburger-container {
  background-color: #ffcf21;
}

.navigation_navbar.open .hamburger-container:hover {
  background-color: white;
}

.navigation_navbar.open .hamburger-container:hover .hamburger span {
  background-color: #273c4e;
}

.navigation_navbar.open .hamburger-container .hamburger span {
  background-color: white;
}

.navigation_navbar.open .hamburger-container .hamburger span:nth-child(1) {
  top: 6px;
  width: 0%;
  left: 50%;
}

.navigation_navbar.open .hamburger-container .hamburger span:nth-child(2) {
  transform: rotate(45deg);
}

.navigation_navbar.open .hamburger-container .hamburger span:nth-child(3) {
  transform: rotate(-45deg);
}

.navigation_navbar.open .hamburger-container .hamburger span:nth-child(4) {
  top: 6px;
  width: 0%;
  left: 50%;
}

.navigation_navbar.open .navigation_navbar-top {
  max-height: 100vh;
}

.navigation_navbar.open .navigation_navbar-top .link {
  top: 0;
  opacity: 1;
  transition: top 400ms 150ms ease-out, opacity 400ms 250ms ease-out;
}

.navigation_navbar.open .navigation_navbar-bottom {
  max-height: 100vh;
}

.navigation_navbar.open .navigation_navbar-bottom .block-nav.navigation_navbar-contact {
  background-color: #273c4e;
  top: 0;
  transition: top 300ms 100ms ease-in-out;
}

.navigation_navbar.open .navigation_navbar-bottom .block-nav.navigation_navbar-decouvrez {
  background-color: #5899f9;
  top: 0;
  transition: top 300ms 200ms ease-in-out;
}

.navigation_navbar.open .navigation_navbar-slide {
  max-height: 100%;
  z-index: 5;
  transition: max-height 500ms ease-in-out;
}

.navigation_navbar .navigation_navbar-bottom {
  max-height: 0;
  display: flex;
  position: fixed;
  bottom: 0;
  right: 0;
  left: 0;
  width: 100%;
  height: 50vh;
  transition: max-height 400ms 100ms ease-in-out;
}

.navigation_navbar .navigation_navbar-bottom .block-nav {
  position: relative;
  color: white;
  padding: 5vh 0 0 5vh;
}

.navigation_navbar .navigation_navbar-bottom .block-nav.navigation_navbar-contact {
  background-color: #273c4e;
  top: 50vh;
  transition: top 300ms ease-in-out;
}

