.navigation_navbar .navigation_navbar-top .link a:hover::after,
.navigation_navbar .navigation_navbar-top .link a:hover::before {
  width: 100%;
  left: 0;
}

.navigation_navbar .navigation_navbar-top .link a::after,
.navigation_navbar .navigation_navbar-top .link a::before {
