","/** Colors */

/** Box Model  */

/** Import everything from autoload */

@import url(./autoload/foundation.min.css);

/**
 * Import npm dependencies
 *
 * Prefix your imports with `~` to grab from node_modules/
 * @see https://github.com/webpack-contrib/sass-loader#imports
 */

/** Import theme styles */

@import url(https://fonts.googleapis.com/css?family=Montserrat:300,400,700,900);

@import url(https://fonts.googleapis.com/css?family=Libre+Baskerville:400i);

body {
  font-family: 'Montserrat', sans-serif;
  font-weight: normal;
  font-size: 16px;
}

.wrap.container {
  padding: 40px;
}

.left-mail {
  transform: rotate(-90deg);
  position: fixed;
  left: -77px;
  top: 50%;
}

.left-mail a {
  color: #273c4e;
}

.right-phone {
  transform: rotate(90deg);
  position: fixed;
  right: -30px;
  top: 50%;
}

.right-phone a {
  color: #273c4e;
}

.i {
  font-family: 'Libre Baskerville', serif;
  font-style: italic;
}

.full-h {
  height: auto;
  min-height: calc(100vh - 202px + 40px);
}

.center-xy {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}

/** Search form */

.navigation_navbar {
  margin-bottom: 90px;
}

.navigation_navbar .logo_digitaleez {
  width: 53px;
  height: 58px;
}

.navigation_navbar.open .hamburger-container {
  background-color: #ffcf21;
}

.navigation_navbar.open .hamburger-container:hover {
  background-color: white;
}

.navigation_navbar.open .hamburger-container:hover .hamburger span {
  background-color: #273c4e;
}

.navigation_navbar.open .hamburger-container .hamburger span {
  background-color: white;
}

.navigation_navbar.open .hamburger-container .hamburger span:nth-child(1) {
  top: 6px;
  width: 0%;
  left: 50%;
}

.navigation_navbar.open .hamburger-container .hamburger span:nth-child(2) {
  transform: rotate(45deg);
}

.navigation_navbar.open .hamburger-container .hamburger span:nth-child(3) {
  transform: rotate(-45deg);
}

.navigation_navbar.open .hamburger-container .hamburger span:nth-child(4) {
  top: 6px;
  width: 0%;
  left: 50%;
}

.navigation_navbar.open .navigation_navbar-top {
  max-height: 100vh;
}

.navigation_navbar.open .navigation_navbar-top .link {
  top: 0;
  opacity: 1;
  transition: top 400ms 150ms ease-out, opacity 400ms 250ms ease-out;
}

.navigation_navbar.open .navigation_navbar-bottom {
  max-height: 100vh;
}

.navigation_navbar.open .navigation_navbar-bottom .block-nav.navigation_navbar-contact {
  background-color: #273c4e;
  top: 0;
  transition: top 300ms 100ms ease-in-out;
}

.navigation_navbar.open .navigation_navbar-bottom .block-nav.navigation_navbar-decouvrez {
  background-color: #5899f9;
  top: 0;
  transition: top 300ms 200ms ease-in-out;
}

.navigation_navbar .navigation_navbar-bottom {
  max-height: 0;
  display: flex;
  position: fixed;
  bottom: 0;
  right: 0;
  left: 0;
  width: 100%;
  height: 50vh;
  transition: max-height 400ms 100ms ease-in-out;
}

.navigation_navbar .navigation_navbar-bottom .block-nav {
  position: relative;
  color: white;
  padding: 5vh 0 0 5vh;
}

.navigation_navbar .navigation_navbar-bottom .block-nav.navigation_navbar-contact {
  background-color: #273c4e;
  top: 50vh;
  transition: top 300ms ease-in-out;
}

.navigation_navbar .navigation_navbar-bottom .block-nav.navigation_navbar-decouvrez {
  background-color: #5899f9;
  top: 50vh;
  transition: top 300ms ease-in-out;
}

.navigation_navbar .navigation_navbar-bottom .block-nav .title {
  font-size: 26px;
  font-weight: bold;
  margin-bottom: 40px;
}

.navigation_navbar .navigation_navbar-bottom .block-nav .sub-title {
  position: relative;
  font-size: 15px;
  font-weight: bold;
  display: inline-block;
  margin-bottom: 25px;
}

.navigation_navbar .navigation_navbar-bottom .block-nav .sub-title:after {
  content: '';
  width: 100%;
  display: block;
  position: absolute;
  bottom: -9px;
  right: 0;
  left: 0;
  background-color: white;
  height: 1px;
}

.navigation_navbar .navigation_navbar-bottom .block-nav .content {
  font-size: 15px;
  text-transform: uppercase;
  letter-spacing: 2px;
}

.navigation_navbar .navigation_navbar-normal {
  top: 40px;
  z-index: 1;
  position: fixed;
  width: 100%;
}

.navigation_navbar .navigation_navbar-normal .link {
  position: absolute;
  top: 20px;
  display: inline-block;
}

.navigation_navbar .navigation_navbar-normal .link.wexperience {
  left: 50px;
}

.navigation_navbar .navigation_navbar-normal .link.logo-degitaleez-container {
  right: 50px;
}

.navigation_navbar .navigation_navbar-normal .link a {
  padding: 0;
}

.navigation_navbar .navigation_navbar-normal .link .logo {
  margin-right: 18px;
}

.navigation_navbar .navigation_navbar-normal .link .logo-text-container {
  vertical-align: -19px;
}

.navigation_navbar .navigation_navbar-normal .link .logo-text-container p {
  top: -4px;
}

.navigation_navbar .navigation_navbar-top {
  height: 100vh;
  max-height: 0;
  transition: max-height 500ms ease-in-out;
  display: flex;
  width: 100%;
  justify-content: center;
  background-color: #ffcf21;
  font-size: 26px;
  overflow: hidden;
}

.navigation_navbar .navigation_navbar-top a {
  padding: 0;
}

.navigation_navbar .navigation_navbar-top .menu {
  display: flex;
  justify-content: space-evenly;
  align-items: center;
  flex-wrap: nowrap;
  max-height: 50vh;
  width: 100%;
  max-width: 1500px;
  margin: 0 50px;
}

.navigation_navbar .navigation_navbar-top .link {
  position: relative;
  font-weight: bold;
  top: 20vh;
  opacity: 0;
  transition: top 400ms ease-out, opacity 400ms ease-out;
}

.navigation_navbar .navigation_navbar-top .link a {
  color: white;
}

.navigation_navbar .navigation_navbar-top .link a:hover::after,
.navigation_navbar .navigation_navbar-top .link a:hover::before {
  width: 100%;
  left: 0;
}

.navigation_navbar .navigation_navbar-top .link a::after,
.navigation_navbar .navigation_navbar-top .link a::before {
  content: '';
  position: absolute;
  top: 50%;
  width: 0;
  right: 0;
  height: 4px;
}

.navigation_navbar .navigation_navbar-top .link a::before {
  transition: width 0.4s cubic-bezier(0.51, 0.18, 0, 0.88) 0.1s;
  background: white;
}

.navigation_navbar .navigation_navbar-top .link a::after {
  transition: width 0.2s cubic-bezier(0.29, 0.18, 0.26, 0.83);
  background: #273c4e;
}

.navigation_navbar .navigation_navbar-top .logo-text {
  position: absolute;
  top: 60px;
  left: 50px;
  z-index: 1;
}

.navigation_navbar .navigation_navbar-top .logo-text p {
  color: white;
}

.navigation_navbar .hamburger {
  margin: 0 auto;
  width: 26px;
  height: 15px;
  position: relative;
  transform: rotate(0deg);
  cursor: pointer;
}

.navigation_navbar .hamburger span {
  display: block;
  position: absolute;
  height: 2px;
  width: 25px;
  background: #273c4e;
  opacity: 1;
  left: 0;
  transform: rotate(0deg);
  transition: .25s ease-in-out;
}

.navigation_navbar .hamburger span:nth-child(1) {
  top: 0px;
}

.navigation_navbar .hamburger span:nth-child(2),
.navigation_navbar .hamburger span:nth-child(3) {
  top: 6px;
}

.navigation_navbar .hamburger span:nth-child(4) {
  top: 12px;
}

.navigation_navbar .navigation_navbar-slide {
  display: block;
  position: fixed;
  width: 100%;
  top: 0;
  right: 0;
  left: 0;
  z-index: 1;
  overflow: hidden;
}

.navigation_navbar .logo-text .logo-text-container {
  display: inline-block;
  vertical-align: -23px;
}

.navigation_navbar .logo-text .logo-text-container p {
  position: relative;
  top: -10px;
  font-size: 14px;
  margin: 0;
}

.navigation_navbar .logo-text .logo {
  margin-right: 15px;
}

.navigation_navbar .logo-text.wexperience .logo {
  width: 55px;
  height: 59px;
}

.navigation_navbar .logo-text.wexperience .text {
  width: 170px;
  height: 24px;
}

.navigation_navbar .logo-text.digitaleez {
  position: absolute;
  top: 50%;
  left: 0;
  text-align: center;
  width: 100%;
  transform: translateY(-30%);
}

.navigation_navbar .logo-text.digitaleez .logo-text-container {
  text-align: left;
}

.navigation_navbar .logo-text.digitaleez .logo {
  width: 107px;
  height: 114px;
}

.navigation_navbar .logo-text.digitaleez .text {
  width: 210px;
  height: 53px;
}

.navigation_navbar .logo-text.digitaleez p {
  color: white;
  top: 6px;
  font-weight: bold;
  text-transform: uppercase;
}

.navigation_navbar .hamburger-container {
  cursor: pointer;
  position: fixed;
  z-index: 5;
  top: 60px;
  right: 50%;
  transform: translateX(50%);
  width: 50px;
  text-align: center;
  height: 50px;
  display: flex;
  align-items: center;
  background-color: white;
  border-radius: 50%;
  transition: .25s ease-in-out;
}

.navigation_navbar .hamburger-container:hover {
  background-color: #ffcf21;
}

.navigation_navbar .hamburger-container:hover .hamburger span {
  background-color: white;
}

@media (max-width: 992px) {
  .navigation_navbar .navigation_navbar-top .menu {
    flex-wrap: wrap;
    align-content: flex-end;
  }

  .navigation_navbar .navigation_navbar-top .menu .link {
    display: block;
    width: 100%;
    margin: 17px 0;
    text-align: center;
  }

  .navigation_navbar .navigation_navbar-bottom .block-nav {
    padding: 2vh 0 0 2vh;
  }

  .navigation_navbar .navigation_navbar-bottom .block-nav .content {
    font-size: 13px;
  }

  .navigation_navbar .logo-text.digitaleez .logo {
    width: 67px;
    height: 73px;
  }

  .navigation_navbar .logo-text.digitaleez .text {
    width: 160px;
    height: 40px;
  }
}

@media (max-height: 950px) and (min-width: 992px) {
  .navigation_navbar .navigation_navbar-bottom {
    height: 65vh;
  }
}

@media (max-width: 767px) {
  .navigation_navbar .navigation_navbar-top,
  .navigation_navbar .navigation_navbar-bottom,
  .navigation_navbar .block-nav {
    height: auto !important;
  }

  .navigation_navbar .navigation_navbar-slide {
    position: absolute;
  }

  .navigation_navbar .navigation_navbar-top {
    flex-direction: column;
    justify-content: baseline;
  }

  .navigation_navbar .navigation_navbar-top .logo-text {
    width: 100%;
    height: auto;
    display: block;
    text-align: center;
    margin-top: 14px;
    position: relative;
    top: 0;
    left: 0;
    text-align: left;
  }

  .navigation_navbar .navigation_navbar-top .logo-text .logo {
    margin-right: 0;
    margin-left: 15px;
  }

  .navigation_navbar .navigation_navbar-top .logo-text .logo-text-container {
    display: none;
  }

  .navigation_navbar .navigation_navbar-top .menu {
